mapper.insert:
	INSERT INTO review_content (review_content, img, tour_loc_id)

            SELECT #{item.content}, #{item.imgSrc}, #{item.tourlocId} 
            FROM DUAL

mapper.select:
select id, review_content as content, title, img as imgSrc, board_id, orders, is_draft
from review_content
where board_id = #{id}
